import React, { useState } from 'react';

const MapSection = () => {
  const [isMapSwapped, setIsMapSwapped] = useState(false);

  // Original map and swapped map
  const map1 = require('./map2.png');
  const map2 = require('./map2.png');

  // Function to toggle between maps
  const handleMapToggle = () => {
    setIsMapSwapped(!isMapSwapped);
  };

  return (
    <section className="map-section text-center">
      <h2 className="text-4xl font-bold mb-10">VISIT US TODAY!</h2>
      <div className="container mt-20 mx-auto px-4 md:px-8 lg:px-12 xl:px-20">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Bigger column */}
          <div className="w-full md:w-3/4">
            <a href="#" onClick={handleMapToggle} rel="noopener noreferrer">
              <img
                src={isMapSwapped ? map2 : map1}
                alt="Location Map"
                className="w-full h-auto rounded-lg shadow-lg mb-8 md:mb-12 mx-auto"
              />
            </a>
          </div>
          {/* Smaller column */}
          <div className="w-full md:w-1/4">
            <a href="#" onClick={handleMapToggle} rel="noopener noreferrer">
              <img
                src={isMapSwapped ? map1 : map2}
                alt="Location Map"
                className="w-full h-auto rounded-lg shadow-lg mb-8 md:mb-12 mx-auto"
              />
            </a>
          </div>
        </div>
      </div>
      <h2 className="text-4xl font-bold">Our Other Locations!</h2>
      {/* Add other location maps as needed */}
    </section>
  );
};

export default MapSection;
